(7714, 1168, 'Combine chicken broth, cornstarch, soy sauce, rice vinegar, Mirin, sesame oil, sugar and Asian chili sauce in a small bowl.', 1, '2023-04-25 06:32:59', '2023-04-25 06:32:59'),
(7715, 1168, 'Preheat non-skillet over medium high heat with 1/2 tablespoon coconut oil.', 2, '2023-04-25 06:32:59', '2023-04-25 06:32:59'),
(7716, 1168, 'Add beef and stir fry until beef is cooked through, 3  4 minutes.', 3, '2023-04-25 06:32:59', '2023-04-25 06:32:59'),
