(7895, 1197, 'After 20 minutes, toss cauliflower pieces to brown all sides.', 6, '2023-04-25 06:32:59', '2023-04-25 06:32:59'),
(7896, 1197, 'Bake for another 20 minutes or until edges are browned.', 7, '2023-04-25 06:32:59', '2023-04-25 06:32:59'),
(7897, 1197, 'Remove from oven and lower temperature to 350 degrees Farenheit.', 8, '2023-04-25 06:32:59', '2023-04-25 06:32:59'),
